LCG={}function LCG:new(a,b,c,d)if type(a)~="number"then error("modulus must be a number")end;if type(b)~="number"then error("multiplier must be a number")end;if type(c)~="number"then error("increment must be a number")end;if type(d)~="number"then error("seed must be a number")end;local e={modulus=a,multiplier=b,increment=c,state=d}setmetatable(e,self)self.__index=self;return e end;function LCG:next()if type(self.state)~="number"then error("internal state is invalid")end;self.state=(self.multiplier*self.state+self.increment)%self.modulus;return self.state end;function LCG:nextFloat()local f=self:next()return f/self.modulus end;function LCG:setSeed(d)if type(d)~="number"then error("seed must be a number")end;self.state=d end
